import React, { useState, useRef, useEffect } from "react";

export default function Word(props) {
  const [solved, setSolved] = useState(false);
  const [guess, setGuess] = useState("");

  const input = useRef(null);
  function isSameWord(w1, w2) {
    return (
      w1.toLowerCase().replace(/[^a-zA-Z ]/g, "") ===
      w2.toLowerCase().replace(/[^a-zA-Z ]/g, "")
    );
  }

  function checkWord(s) {
    setGuess(s);
    if (isSameWord(guess, props.word)) {
      setSolved(true);
    } else {
    }
  }

  return (
    <input
      ref={input}
      type="text"
      onChange={(event) => checkWord(event)}
      value={guess}
    ></input>
  );
}
