import React, {
  useState,
  useRef,
  useEffect,
  useCallback,
  useMemo,
} from "react";
import Word from "./Word";

export default function Question(props) {
  // Declare a new state variable, which we'll call "count"
  const [activeWord, setActiveWord] = useState(0);
  const renderedWords = useRef(null);

  const toNextWord = useCallback(() => {
    setActiveWord(activeWord + 1);
  }, [activeWord]);

  function parseWords(sentence) {
    let words = sentence.split(" ");
    let renderedWords = [];
    for (let i in words) {
      renderedWords.push(
        <Word
          key={i}
          index={i}
          word={words[i]}
          toNextWord={toNextWord}
          active={activeWord}
        ></Word>
      );
    }
    return renderedWords;
  }
  const missingWords = parseWords(props.missingLyrics);

  useEffect(
    function solveQuestion() {
      if (activeWord === missingWords.length) {
        console.log("solved!");
      } else {
        renderedWords.current.childNodes[activeWord].focus();
      }
    },
    [activeWord, missingWords.length]
  );

  return (
    <div>
      <div>{props.givenLyrics}</div>
      <div ref={renderedWords}>{missingWords}</div>
    </div>
  );
}
