import React, { useState, useEffect, useRef } from "react";
import style from "./style.module.css";

export default function Word({ word, toNextWord, active, index }) {
  const inputElement = useRef(null);

  const [guess, setGuess] = useState("");
  const [solved, markSolved] = useState(false);

  useEffect(
    function checkWords() {
      console.log(guess);
      if (guess[guess.length - 1] === " ") {
        setGuess(guess.slice(0, guess.length - 1));
        console.log("space tried");
        inputElement.current.classList.toggle("shake");
        inputElement.current.classList.toggle("shake");

        // inputElement.animate(
        //   [
        //     // keyframes
        //     { transform: "translateY(0px)" },
        //     { transform: "translateY(-300px)" },
        //   ],
        //   {
        //     // timing options
        //     duration: 1000,
        //     iterations: Infinity,
        //   }
        // );
      } else {
        if (
          active == index &&
          guess.toLowerCase().replace(/[^a-zA-Z ]/g, "") ===
            word.toLowerCase().replace(/[^a-zA-Z ]/g, "")
        ) {
          markSolved(true);
          toNextWord();
        }
      }
    },
    [guess, word, toNextWord, active, index]
  );

  useEffect(
    function focusInput() {
      if (active == index) {
        inputElement.current.focus();
      }
    },
    [active, index]
  );

  return (
    <input
      type="text"
      className={style.blank}
      disabled={active != index}
      style={{
        color: solved ? "green" : "",
        borderColor: solved ? "green" : "",
      }}
      ref={inputElement}
      onChange={(event) => setGuess(event.target.value)}
      value={guess}
    />
  );
}
