import React, { useState, useRef, useEffect, useCallback } from "react";
import Word from "./Word";

export default function Question({ givenLyrics, missingLyrics }) {
  // Declare a new state variable, which we'll call "count"

  const renderedWords = useRef(null);
  const [activeWord, setActiveWord] = useState(0);
  const toNextWord = useCallback(() => {
    setActiveWord(activeWord + 1);
  }, [activeWord]);

  const missingWords = [];
  useEffect(
    function parseWords() {
      let words = missingLyrics.split(" ");
      let renderedWords = [];
      for (let i in words) {
        renderedWords.push(
          <Word key={i} word={words[i]} toNextWord={toNextWord}></Word>
        );
      }
      setMissingWords(renderedWords);
    },
    [missingWords, toNextWord, missingLyrics]
  );

  useEffect(
    function solveQuestion() {
      if (activeWord === missingWords.length) {
        console.log("solved!");
      } else {
        renderedWords.current.childNodes[activeWord].focus();
      }
    },
    [activeWord, missingWords.length]
  );

  return (
    <div>
      <div>{givenLyrics}</div>
      <div ref={renderedWords}>{missingWords}</div>
    </div>
  );
}
