import React, { useState, useRef, useEffect, useCallback } from "react";
import Word from "./Word";

export default function Question({
  givenLyrics,
  missingLyrics,
  activeQuestion,
  index,
  skip,
  solve,
}) {
  const renderedWords = useRef(null);

  const [activeWord, setActiveWord] = useState(-1);
  const toNextWord = useCallback(() => {
    setActiveWord(activeWord + 1);
  }, [activeWord]);

  useEffect(() => {
    if (activeQuestion == index) {
      setActiveWord(0);
    }
  }, [activeQuestion, index]);

  function parseWords(sentence) {
    let words = sentence.split(" ");
    let renderedWords = [];
    for (let i in words) {
      renderedWords.push(
        <Word
          key={i}
          index={i}
          word={words[i]}
          toNextWord={toNextWord}
          active={activeWord}
        ></Word>
      );
    }
    return renderedWords;
  }
  const missingWords = parseWords(missingLyrics);

  const [solved, markSolved] = useState(false);
  useEffect(
    function solveQuestion() {
      if (!solved && activeWord === missingWords.length) {
        console.log("solved");
        markSolved(true);
        solve();
      }
    },
    [activeWord, missingWords.length, solve, solved]
  );

  return (
    <div style={{ display: activeQuestion == index ? "inline-block" : "none" }}>
      <div>{givenLyrics}</div>
      <div ref={renderedWords}>{missingWords}</div>
      <button style={{ display: solved ? "none" : "block" }}>SKIP</button>
      <button style={{ display: activeQuestion == index ? "block" : "none" }}>
        NEXT
      </button>
    </div>
  );
}
